import react ,{ useState } from "react";
import './ChartBar.css'

const ChartBar = (props) =>
{
    let barHeightFiller='0%';
    if(props.maxValue > 0){
    barHeightFiller = Math.round(Math.abs((props.value/props.maxValue))*100) + '%';
    }
    return (
        <div className="chart-bar">
            <div className="chart-bar__inner">
                <div className="chart-bar__fill" style={{height: barHeightFiller}}></div>
            </div>
            <div className="chart-bar__label">{props.label}</div>
        </div>
    )
}

export default ChartBar;
