import { useState } from 'react/cjs/react.development';

function Expenses(props)
{
    let [currYear, setCurrYear] = useState('2019');
    const onDateChange = (year) =>
    {
        currYear = year;
        setCurrYear(year);
        console.log(currYear);
    }
    return (
      <div>
        <Card className="expenses">
        <ExpensesFilter selected={currYear} OnYearChange={onDateChange}></ExpensesFilter>
        {props.expenses.map((ele,i)=>
          {
            <ExpenseItem
            title={props.expenses[i].title}
            amount={props.expenses[i].amount}
            date={props.expenses[i].date}
